commits.sourceNew:
/**
 * Closes the BytesRefHash and releases all internally used memory
 */
public void close() {
  clear(true);
  ords = null;
  bytesUsed.addAndGet(RamUsageEstimator.NUM_BYTES_INT
      * -hashSize);
}

