private static BytesRef longToBytes(long v) {
  // Flip the sign bit so negative longs sort before positive longs:
  v ^= 0x8000000000000000L;
  byte[] bytes = new byte[8];
  bytes[0] = (byte) (v >> 56);
  bytes[1] = (byte) (v >> 48);
  bytes[2] = (byte) (v >> 40);
  bytes[3] = (byte) (v >> 32);
  bytes[4] = (byte) (v >> 24);
  bytes[5] = (byte) (v >> 16);
  bytes[6] = (byte) (v >> 8);
  bytes[7] = (byte) v;
  return new BytesRef(bytes);
}

