  assertU(commit());

  reader = core.getSearcher().get().getReader();
  map = comp.getElevationMap(reader, core);
  assertNull( map.get( "aaa" ) );
  assertTrue( map.get( "bbb" ).priority.containsKey( "B" ) );
}

