  int pos = -1;
  for (Token tap : INCR_0_QUERY_TOKENS_AND) {
    pos += tap.getPositionIncrement();
    builder.add(new Term("field", tap.toString()), pos);
  }
  builder.setSlop(0);
  doTestZeroPosIncrSloppy(builder.build(), 0);
  builder.setSlop(1);
  doTestZeroPosIncrSloppy(builder.build(), 0);
  builder.setSlop(2);
  doTestZeroPosIncrSloppy(builder.build(), 1);
}

