    uniqueValues = new HashSet<>();

    for (Number nv : values) {
      final long v;
      if (nv == null) {
        v = 0;
        missingCount++;
        zeroCount++;
      } else {
        v = nv.longValue();
        if (v == 0) {
          zeroCount++;
