      meta.writeVInt(bits);
      final DirectWriter quotientWriter = DirectWriter.getInstance(data, count, bits);
      for (Number nv : values) {
        long value = nv == null ? 0 : nv.longValue();
        quotientWriter.add((value - minValue) / gcd);
      }
      quotientWriter.finish();
      break;
