private void joinAll(final CreateDeleteCollectionThread[] threads) {
  for (CreateDeleteCollectionThread t : threads) {
    try {
      t.joinAndClose();
    } catch (InterruptedException e) {
      Thread.interrupted();
      throw new RuntimeException(e);
    }
  }
}

