  // get core open times
  Map<String,Long> urlToTimeBefore = new HashMap<>();
  collectStartTimes(collectionName, urlToTimeBefore);
  assertTrue(urlToTimeBefore.size() > 0);
  ModifiableSolrParams params = new ModifiableSolrParams();
