  final Collection<? extends Closeable> toClose;
  if (useCompoundFile) {
    final ArrayList<Closeable> list = new ArrayList<Closeable>(closeables);
    list.add(cfs);
    toClose = list; 
  } else {
    toClose = docValues.values();

  } 
  IOUtils.closeSafely(false, toClose);
}

