/**
 * Gets the default slop for phrases.
 */
public int getPhraseSlop() {
  Integer phraseSlop = getQueryConfigHandler().get(ConfigurationKeys.PHRASE_SLOP);

  if (phraseSlop == null) {
    return 0;

  } else {
    return phraseSlop;
  }
}

