public Direct8(IndexInput in, int valueCount)
        throws IOException {
  super(valueCount, BITS_PER_VALUE);
  byte[] blocks = new byte[valueCount];
  for(int i=0;i<valueCount;i++) {
    blocks[i] = in.readByte();
  }
  final int mod = valueCount % 8;
  if (mod != 0) {
    final int pad = 8-mod;
    // round out long
    for(int i=0;i<pad;i++) {
      in.readByte();
    }
  }

  this.blocks = blocks;
}

