public void testSimilarity() throws Exception {
  RAMDirectory store = new RAMDirectory();
  IndexWriter writer = new IndexWriter(store, new SimpleAnalyzer(), true, 
                                       IndexWriter.MaxFieldLength.LIMITED);
  writer.setSimilarity(new SimpleSimilarity());

  Document d1 = new Document();
  d1.add(new Field("field", "a c", Field.Store.YES, Field.Index.ANALYZED));

  Document d2 = new Document();
  d2.add(new Field("field", "a b c", Field.Store.YES, Field.Index.ANALYZED));

  writer.addDocument(d1);
  writer.addDocument(d2);
  writer.optimize();
  writer.close();

  Searcher searcher = new IndexSearcher(store, true);
  searcher.setSimilarity(new SimpleSimilarity());

  Term a = new Term("field", "a");
  Term b = new Term("field", "b");
  Term c = new Term("field", "c");

  searcher.search(new TermQuery(b), new Collector() {
       private Scorer scorer;
       @Override
      public void setScorer(Scorer scorer) throws IOException {
         this.scorer = scorer; 
       }
       @Override
      public final void collect(int doc) throws IOException {
         assertEquals(1.0f, scorer.score());
       }
       @Override
      public void setNextReader(IndexReader reader, int docBase) {}
       @Override
      public boolean acceptsDocsOutOfOrder() {
         return true;
       }
     });

  BooleanQuery bq = new BooleanQuery();
  bq.add(new TermQuery(a), BooleanClause.Occur.SHOULD);
  bq.add(new TermQuery(b), BooleanClause.Occur.SHOULD);
  //System.out.println(bq.toString("field"));
  searcher.search(bq, new Collector() {
       private int base = 0;
       private Scorer scorer;
       @Override
      public void setScorer(Scorer scorer) throws IOException {
         this.scorer = scorer; 
       }
       @Override
      public final void collect(int doc) throws IOException {
         //System.out.println("Doc=" + doc + " score=" + score);
         assertEquals((float)doc+base+1, scorer.score());
       }
       @Override
      public void setNextReader(IndexReader reader, int docBase) {
         base = docBase;
       }
       @Override
      public boolean acceptsDocsOutOfOrder() {
         return true;
       }
     });

  PhraseQuery pq = new PhraseQuery();
  pq.add(a);
  pq.add(c);
  //System.out.println(pq.toString("field"));
  searcher.search(pq,
     new Collector() {
       private Scorer scorer;
       @Override
       public void setScorer(Scorer scorer) throws IOException {
        this.scorer = scorer; 
       }
       @Override
       public final void collect(int doc) throws IOException {
         //System.out.println("Doc=" + doc + " score=" + score);
         assertEquals(1.0f, scorer.score());
       }
       @Override
       public void setNextReader(IndexReader reader, int docBase) {}
       @Override
       public boolean acceptsDocsOutOfOrder() {
         return true;
       }
     });

  pq.setSlop(2);
  //System.out.println(pq.toString("field"));
  searcher.search(pq, new Collector() {
    private Scorer scorer;
    @Override
    public void setScorer(Scorer scorer) throws IOException {
      this.scorer = scorer; 
    }
    @Override
    public final void collect(int doc) throws IOException {
      //System.out.println("Doc=" + doc + " score=" + score);
      assertEquals(2.0f, scorer.score());
    }
    @Override
    public void setNextReader(IndexReader reader, int docBase) {}
    @Override
    public boolean acceptsDocsOutOfOrder() {
      return true;
    }
  });
}

