private static ArrayList<File> rm(File location, ArrayList<File> unremoved) {
  if (location.exists()) {
    if (location.isDirectory()) {
      for (File f : location.listFiles()) {
        rm(f, unremoved);
      }
    }

    if (!location.delete()) {
      unremoved.add(location);
    }
  }
  return unremoved;
}

