@Override
public Number getSpecificValue(String dim, String... path) {
  if (path.length != 1) {
    throw new IllegalArgumentException("path must be length=1");
  }

  int ord = (int) dv.lookupTerm(new BytesRef(dim + state.separator + path[0]));
  if (ord < 0) {
    return -1;
  }

  return counts[ord];
}

