/** Returns the total size of all the suggester */
public long sizeInBytes() {
  long sizeInBytes = 0;
  for (SolrSuggester suggester : suggesters.values()) {
    sizeInBytes += suggester.sizeInBytes();
  }
  return sizeInBytes;
}
