private void assertTokEq(List a, List b) {
  int pos=0;
  for (Iterator iter = a.iterator(); iter.hasNext();) {
    Token tok = (Token)iter.next();
    pos += tok.getPositionIncrement();
    if (!tokAt(b, tok.termText(), pos)) {
      fail(a + "!=" + b);
    }
  }
}

