public boolean skipTo(int target) throws IOException {
  // are we already at the correct position?
  if (doc >= target) {
    return true;
  }

  if (!positions.skipTo(target)) {
    doc = Integer.MAX_VALUE;
    return false;
  }

  doc = positions.doc();
  freq = positions.freq();
  count = 0;

  position = positions.nextPosition();
  count++;

  return true;
}

