/** Returns total size in bytes of all files for this
 *  segment. */
public long sizeInBytes() throws IOException {
  if (sizeInBytes == -1) {
    long sum = 0;
    for (final String fileName : files()) {
      sum += info.dir.fileLength(fileName);
    }
    sizeInBytes = sum;
  }

  return sizeInBytes;
}

