/**
 * Checks if a term could be stemmed.
 *
 * @return  true if, and only if, the given term consists in letters.
 */
private boolean isStemmable( String term )
{
  for ( int c = 0; c < term.length(); c++ ) {
    if ( !Character.isLetter( term.charAt( c ) ) )
      return false;
  }
  return true;
}

