public String toString(String field) {
  StringBuffer buffer = new StringBuffer();
  buffer.append("mask(");
  buffer.append(maskedQuery.toString(field));
  buffer.append(")");
  buffer.append(ToStringUtils.boost(getBoost()));
  buffer.append(" as ");
  buffer.append(this.field);
  return buffer.toString();
}

