@Override
public void run() {
  try {
    overseerClient = electNewOverseer(zkAddress);
    Random rnd = random();
    while (run) {
      if (killCounter.get()>0) {
        try {
          killCounter.decrementAndGet();
          log.info("Killing overseer.");
          overseerClient.close();
          overseerClient = electNewOverseer(zkAddress);
        } catch (Throwable e) {
          // e.printStackTrace();
        }
      }
      try {
        Thread.sleep(100);
      } catch (Throwable e) {
        // e.printStackTrace();
      }
    }
  } catch (Throwable t) {
    // ignore
  } finally {
    if (overseerClient != null) {
      try {
        overseerClient.close();
      } catch (Throwable t) {
        // ignore
      }
    }
  }
}

