public Tuple read() throws IOException {
  if(shortestPaths.size() > 0) {
    found = true;
    Tuple t = shortestPaths.removeFirst();
    return t;
  } else {
    Map m = new HashMap();
    m.put("EOF", true);
    if(!found) {
      m.put("sorry", "No path found");
    }
    return new Tuple(m);
  }
}

