private synchronized void clearPriorSnapshots() throws IOException {
  for(String file : dir.listAll()) {
    if (file.startsWith(SNAPSHOTS_PREFIX)) {
      dir.deleteFile(file);
    }
  }
}

private synchronized void clearPriorSnapshots() throws IOException {
  for(String file : dir.listAll()) {
    if (file.startsWith(SNAPSHOTS_PREFIX)) {
      dir.deleteFiles(Collections.singleton(file));
    }
  }
}

