/**
 * Compute the number of bits required to serialize any of the longs in
 * <code>data</code>.
 */
private static int bitsRequired(final int[] data) {
  long or = 0;
  for (int i = 0; i < BLOCK_SIZE; ++i) {
    or |= data[i];
  }
  return PackedInts.bitsRequired(or);
}

