public void setLength(int newLength) {
  if (result.length < newLength) {
    byte[] newArray = new byte[(int) (1.5*newLength)];
    System.arraycopy(result, 0, newArray, 0, length);
    result = newArray;
  }
  length = newLength;
}

public void setLength(int newLength) {
  if (result.length < newLength) {
    result = ArrayUtil.grow(result, newLength);
  }
  length = newLength;
}

