private static void swapCodec(Codec c) {
  final CodecProvider cp = CodecProvider.getDefault();
  Codec prior = null;
  try {
    prior = cp.lookup(c.name);
  } catch (IllegalArgumentException iae) {
  }
  if (prior != null) {
    cp.unregister(prior);
  }
  cp.register(c);
}

