public Direct32(DataInput in, int valueCount) throws IOException {
  super(valueCount, BITS_PER_VALUE);
  int[] values = new int[valueCount];
  for(int i=0;i<valueCount;i++) {
    values[i] = in.readInt();
  }
  final int mod = valueCount % 2;
  if (mod != 0) {
    in.readInt();
  }

  this.values = values;
}

