/*
 * Full width alphas and numerics are folded to half-width
 */
public void testFullWidth() throws Exception {
  assertAnalyzesTo(new SmartChineseAnalyzer(true), "我购买 Ｔｅｓｔｓ 了道具和服装１２３４",
      new String[] { "我", "购买", "test", "了", "道具", "和", "服装", "1234"});
}

