private String toString(Reader reader) throws IOException {
  StringBuilder stringBuilder = new StringBuilder();
  int ch;
  while ((ch = reader.read()) > -1) {
    stringBuilder.append((char) ch);
  }
  return stringBuilder.toString();
}

