@Override
public boolean isWithin(final double x, final double y, final double z) {
  if (circlePlanes == null) {
    return true;
  }
  for (final Membership plane : circlePlanes) {
    if (!plane.isWithin(x, y, z)) {
      return false;
    }
  }
  return true;
}

