public void testNoDir() throws Throwable {
  Directory dir = newFSSwitchDirectory(Collections.<String>emptySet());
  try {
    IndexReader.open(dir, true);
    fail("did not hit expected exception");
  } catch (NoSuchDirectoryException nsde) {
    // expected
  }
  dir.close();
}

