  assertTrue(tuples.size() == 1);
  List<Number> sequence = (List<Number>)tuples.get(0).get("seq");
  assertTrue(sequence.size() == 20);
  for(int i=0; i<sequence.size(); i++) {
    assertTrue(sequence.get(i).intValue() == i);
  }

  //Change the size, stride
