final void createDir() throws IOException {
  if (!checked) {
    if (directory.exists() && !directory.isDirectory())
      throw new IOException(directory + " not a directory");

    if (!directory.exists())
      if (!directory.mkdirs())
        throw new IOException("Cannot create directory: " + directory);

    checked = true;
  }
}

