/** {@inheritDoc} */
@Override
public String toString() {
  final StringBuilder buffer = new StringBuilder();
  if (hasChanges) {
    buffer.append("*");
  }
  buffer.append(getClass().getSimpleName());
  buffer.append('(');
  for(SegmentReader r : subReaders) {
    buffer.append(r);
  }
  buffer.append(')');
  return buffer.toString();
}

