private void addBlock(byte[] block) {
  if (blocks.length == numBlocks) {
    blocks = Arrays.copyOf(blocks, ArrayUtil.oversize(numBlocks, RamUsageEstimator.NUM_BYTES_OBJECT_REF));
  }
  blocks[numBlocks++] = block;
}

