private Replica getReplica(DocCollection coll, String replicaName) {
  if (coll == null) return null;
  for(Slice slice: coll.getSlices()) {
    Replica replica = slice.getReplica(replicaName);
    if (replica != null) return replica;
  }
  return null;
}

