/** 
 * Organize subScorers into a min heap with scorers generating the earliest document on top.
 */
protected final void minheapHeapify() {
  for (int i = (nrInHeap >> 1) - 1; i >= 0; i--) {
    minheapAdjust(i);
  }
}

/**
 * Organize subScorers into a min heap with scorers generating the earliest document on top.
 */
protected final void minheapHeapify() {
  for (int i = (nrInHeap >> 1) - 1; i >= 0; i--) {
    minheapSiftDown(i);
  }
}

