byte[] bytes() throws IOException {
    byte[] bytes = new byte[maxdoc];
    // some norms share fds
    synchronized(file) {
      file.seek(offset);
      file.readBytes(bytes, 0, bytes.length, false);
    }
    // we are done with this file
    if (file != singleNormStream) {
      openFiles.remove(file);
      file.close();
    }
  return bytes;
}

