  } finally {
    solrServer.shutdown();
  }

  CollectionAdminRequest.Delete deleteCollectionRequest = new CollectionAdminRequest.Delete();
  deleteCollectionRequest.setCollectionName(collectionName);
  deleteCollectionRequest.process(cloudClient);
}

