            if (VERBOSE) {
              log.println("    now split on z=" + splitValue);
            }
            Cell cell1 = new Cell(cell,
                               cell.xMinEnc, cell.xMaxEnc,
                               cell.yMinEnc, cell.yMaxEnc,
                               cell.zMinEnc, splitValue,
