              log.println("    split cell2: " + cell2);
            }
            queue.add(cell1);
            queue.add(cell2);
          }
          break;
        }
      }
    }

    if (VERBOSE) {
      log.println("  " + hits.size() + " hits");
    }

    // Done matching, now verify:
    boolean fail = false;
    for(int docID=0;docID<numDocs;docID++) {
      GeoPoint point = docs[docID];
      GeoPoint quantized = quantize(planetMax, point);
