/*
 * Numerics are parsed as their own tokens
 */
public void testNumerics() throws Exception {
  assertAnalyzesTo(new SmartChineseAnalyzer(true), "我购买 Tests 了道具和服装1234",
    new String[] { "我", "购买", "test", "了", "道具", "和", "服装", "1234"});
}

