  log.info("####initCore");
  dataDir = new File(System.getProperty("java.io.tmpdir")
          + System.getProperty("file.separator")
  //        + getClass().getName() + "-" + System.currentTimeMillis());
          + System.currentTimeMillis());
  dataDir.mkdirs();

  String configFile = getSolrConfigFile();
