commits.sourceNew:
private void writeAttribute(Writer w, String name, Object value)
    throws IOException {
  if (value == null) return;
  w.write(" ");
  w.write(name);
  w.write("=\"");
  XML.escapeAttributeValue(value.toString(), w);
  w.write("\"");
}

