/**
 * Commit changes resulting from delete, undeleteAll, or setNorm operations
 * <p/>
 * If an exception is hit, then either no changes or all changes will have been committed to the index (transactional
 * semantics).
 *
 * @throws IOException if there is a low-level IO error
 */
protected void doCommit(Map commitUserData) throws IOException {
  if (hasChanges) {
    segmentInfos.setUserData(commitUserData);
    // Default deleter (for backwards compatibility) is
    // KeepOnlyLastCommitDeleter:
    IndexFileDeleter deleter = new IndexFileDeleter(directory,
                                                    deletionPolicy == null ? new KeepOnlyLastCommitDeletionPolicy() : deletionPolicy,
                                                    segmentInfos, null, null);

    // Checkpoint the state we are about to change, in
    // case we have to roll back:
    startCommit();

    boolean success = false;
    try {
      for (int i = 0; i < subReaders.length; i++)
        subReaders[i].commit();

      // Sync all files we just wrote
      Iterator it = segmentInfos.files(directory, false).iterator();
      while (it.hasNext()) {
        final String fileName = (String) it.next();
        if (!synced.contains(fileName)) {
          assert directory.fileExists(fileName);
          directory.sync(fileName);
          synced.add(fileName);
        }
      }

      segmentInfos.commit(directory);
      success = true;
    } finally {

      if (!success) {

        // Rollback changes that were made to
        // SegmentInfos but failed to get [fully]
        // committed.  This way this reader instance
        // remains consistent (matched to what's
        // actually in the index):
        rollbackCommit();

        // Recompute deletable files & remove them (so
        // partially written .del files, etc, are
        // removed):
        deleter.refresh();
      }
    }

    // Have the deleter remove any now unreferenced
    // files due to this commit:
    deleter.checkpoint(segmentInfos, true);
    deleter.close();

    if (writeLock != null) {
      writeLock.release();  // release write lock
      writeLock = null;
    }
  }
  hasChanges = false;
}

