@Override
public void inform(SolrCore core) {
  super.inform(core);
  parser = new PreAnalyzedField();
  Map<String,String> args = new HashMap<String,String>();
  if (parserImpl != null) {
    args.put(PreAnalyzedField.PARSER_IMPL, parserImpl);
  }
  parser.init(core.getSchema(), args);
}

