private Query clause( String field, float boost, String... terms ) {
  Query q;
  if ( terms.length == 1 ) {
    q = new TermQuery( new Term( field, terms[ 0 ] ) );
  } else {
    PhraseQuery pq = new PhraseQuery();
    for ( String term: terms ) {
      pq.add( new Term( field, term ) );
    }
    q = pq;
  }
  q.setBoost( boost );
  return q;
}

