private synchronized SegmentReader doGetReader(IOContext context) throws IOException {
  if (reader == null) {
    // We steal returned ref:
    reader = new SegmentReader(info, context);
    if (liveDocs == null) {
      liveDocs = reader.getLiveDocs();
    }
  }

  // Ref for caller
  reader.incRef();
  return reader;
}

