//  assumes n > 0
//  64 bit VM:
//    0     --> 0*PTR
//    1..8  --> 1*PTR
//    9..16 --> 2*PTR
private static int sizeOf(int n) {
    return IS_WORD_ALIGNED_VM ?
            ((n-1)/PTR + 1) * PTR :
            n;
}

