public static float[] grow(float[] array, int minSize) {
  if (array.length < minSize) {
    float[] newArray = new float[oversize(minSize, RamUsageEstimator.NUM_BYTES_FLOAT)];
    System.arraycopy(array, 0, newArray, 0, array.length);
    return newArray;
  } else
    return array;
}

